def main():
  dataset = 'MNIST' #['fmnist', 'cifar10', 'cifar100']
  n_task = 5
  colors = ['limegreen', 'hotpink', 'blueviolet', 'royalblue', 'darkorange', 'gold', 'brown']
  data = get_data()

  fig, axs = plt.subplots(n_task, 1)
  
  for item in data:
    label = item['label']
    acc = item['acc']

    for i in range(n_task):
      axs[i].plot(np.arange(i*30, 150, 1), acc['task_{}'.format(i)], label=label)
      axs[i].set_ylim([65, 100])
      axs[i].set_ylabel('Task {} Accuracy'.format(i), fontsize=12, ha='right')
      axs[i].set_xlabel('Training step')
      axs[i].set_yticks(np.arange(70, 101, step=10))
      axs[i].set_xticks(np.arange(0, 151, step=10))
    
    axs[i].legend(loc='best')

  plt.show()
  


if __name__ == '__main__':
  main()


